controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (currentWeapon == 0) {
        music.playTone(262, music.beat(BeatFraction.Sixteenth))
        launchSoap()
    } else if (currentWeapon == 1) {
        music.jumpUp.play()
        launchMasks()
        if (masksRemaining == 0) {
            currentWeapon = 0
            changeWeapon(0)
        }
    } else {
        music.jumpDown.play()
        lauchLockdown()
        currentWeapon = 0
        changeWeapon(0)
    }
})
function initialiseWeapons () {
    weapons = ["soap", "masks", "lockdown"]
    masksRemaining = 2
    lockdownsRemaining = 1
}
