function bumpCrate (person: Sprite, crate: Sprite) {
    person.setFlag(SpriteFlag.Ghost, true)
    randomVelocity(person, MAX_PERSON_VELOCITY)
    randomVelocity(crate, MAX_CRATE_VELOCITY)
    pause(200)
    person.setFlag(SpriteFlag.Ghost, false)
    crate.setVelocity(0, 0)
}
