sprites.onOverlap(SpriteKind.Healthy, SpriteKind.Infected, function (sprite, otherSprite) {
    infectionRate = BASE_INFECTION_RATE
    if (sprites.readDataBoolean(sprite, "hasMask?") && sprites.readDataBoolean(otherSprite, "hasMask?")) {
        infectionRate = 1
    } else if (sprites.readDataBoolean(sprite, "hasMask?") || sprites.readDataBoolean(otherSprite, "hasMask?")) {
        infectionRate = MASK_INFECTION_RATE
    }
    if (Math.percentChance(infectionRate)) {
        sprite.setKind(SpriteKind.Infected)
        sprite.image.replace(7, 2)
    }
    bumpPeople(sprite, otherSprite)
})
function randomVelocity (person: Sprite, maxVelocity: number) {
    newVx = randint(0 - maxVelocity, maxVelocity)
    newVy = randint(0 - maxVelocity, maxVelocity)
    person.setVelocity(newVx, newVy)
    if (sprites.readDataBoolean(person, "hasMask?")) {
        sprites.readDataSprite(person, "mask").setVelocity(newVx, newVy)
    }
}
